package com.cloud.common.utils;import java.util.List;public class CommonUtils {    /**     * 判断实体对象是否为空     * @param object     * @return     */    public static boolean isNullEntery(Object object){        if(null==object){            return true;        }        return false;    }    /**     * 判断list是否为空     * @param list     * @return     */    public static boolean isNullList(List list){        if(list.size()==0){            return true;        }        return false;    }}