package com.cloud.common.utils;public class ResponseUtil {    private  static final String SUCCESS_CODE = "000000";    private static final String FAIL_CODE = "999999";    private static final String OTHER_CODE = "111111";    private static final String SUCCESS_MSG = "请求成功";    private static final String FAIL_MSG = "请求失败";    private static final String OTHER_MSG = "未知请求";    private ReturnMsg returnMsg = new ReturnMsg();    public ReturnMsg isSuccess(){        returnMsg.setCode(SUCCESS_CODE);        returnMsg.setMsg(SUCCESS_MSG);        return returnMsg;    }    public ReturnMsg isFail(){        returnMsg.setCode(FAIL_CODE);        returnMsg.setMsg(FAIL_MSG);        return returnMsg;    }    public ReturnMsg isOther(){        returnMsg.setCode(OTHER_CODE);        returnMsg.setMsg(OTHER_MSG);        return returnMsg;    }}