package com.cloud.common.utils;public class ReturnMsg {    private String code;    private String msg;    private Object entity = null;    public String getCode() {        return code;    }    public void setCode(String code) {        this.code = code;    }    public String getMsg() {        return msg;    }    public void setMsg(String msg) {        this.msg = msg;    }    public Object getEntity() {        return entity;    }    public void setEntity(Object entity) {        this.entity = entity;    }}